import React from 'react'
import { VStack, ButtonGroup, Heading, FormControl, FormLabel, Button, 
         FormErrorMessage, Input} from '@chakra-ui/react';

import {useFormik} from 'formik';
import * as Yup from 'yup';

const Login = () => {
    const formik = useFormik({
        initalValues: {username: "", password: ""},
        validationSchema: Yup.object({
          username: Yup.string().required("Username required!")
                                .min(6,"Username too short!")
                                .max(28,"Username too long!"),
          password: Yup.string().required("Password required!")
                                .min(6,"Password too short!")
                                .max(28,"Password too long!")

        }),
        onSubmit: (values, actions) => {
          alert(JSON.stringify(values, null, 2));
          actions.resetForm();
        }
    })
  return  <VStack as="form" 
                  w={{base:"90%", 
                  md: "500px"}} 
                  m="auto"
                  justify="center" 
                  h="100vh" 
                  spacing='1rem'
                  onSubmit={formik.handleSubmit}>

            <Heading>
              Log In
            </Heading>

            <FormControl isInvalid={formik.errors.username && formik.touched.username}>
              <FormLabel fontSize='lg'>Username</FormLabel>  
              <Input  name='username' 
                      placeholder='Enter username'
                      autoComplete='off' 
                      size='lg'
                      value={formik.values.username}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}/>
              <FormErrorMessage>Invalid username</FormErrorMessage>
            </FormControl>

            <FormControl isInvalid={formik.errors.password && formik.touched.password}>
              <FormLabel fontSize='lg'>Password</FormLabel>  
              <Input  name='password'
                      placeholder='Enter password'
                      autoComplete='off' 
                      size='lg'
                      type='password'
                      value={formik.values.password}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}/>
              <FormErrorMessage>Invalid password</FormErrorMessage>

            </FormControl>

            <ButtonGroup pt="1rem">
              <Button colorScheme='teal' type='submit'>Log In</Button>
              <Button>Create Account</Button>
            </ButtonGroup>
          </VStack>
}

export default Login;
